(8507, 433, 'accordion_section_0_nested_accordion_1_nested_accordion_title', 'Planting Container Stock'),
(8508, 433, '_accordion_section_0_nested_accordion_1_nested_accordion_title', 'field_53a2f969f2703'),
(8509, 433, 'accordion_section_0_nested_accordion_1_nested_accordion_color', '#dd3333'),
(8510, 433, '_accordion_section_0_nested_accordion_1_nested_accordion_color', 'field_53a2f989f2704'),
(8511, 433, 'accordion_section_0_nested_accordion_1_nested_accordion_content', ' Plant throughout the growing season, but increase survival rates by planting in spring and fall, especially during wet periods\r\n\r\n Water plants well the day before planting\r\n\r\n Remove the plant from the container, be careful not to damage the stem\r\n\r\n Separate some roots away from the root ball to help with new growth\r\n\r\n Dig a wide hole at least 2-3 times the container width, and about 8 inches deep for every 12 inches of root growth\r\n\r\n Put some topsoil or compost at the bottom of the hole\r\n\r\n Make sure to plant at the same soil depth as the container\r\n\r\n Fill the hole with soil and pack down well to remove air pockets'),
